$(".in-comment .btn").on("click",function(){if(name=$(".in-comment .name").val(),content=$(".in-comment .content").val(),!name)return void alert("请输入姓名");if(!content)return void alert("请输入内容");var id=$("#blogID").val();$.ajax({type:"post",url:"/blog/comment",data:{blogID:id,name:name,content:content,addTime:new Date},success:function(data){data&&(alert("评论提交成功"),location.reload())}})});var $right=$("#right");$right.length&&($prev=$right.prev(),$right.css("top",$prev.offset().top),$right.css("left",$prev.offset().left+$prev.outerWidth()+10),$right.css("position","absolute"),$(document).on("scroll",function(){$(document).scrollTop()>=$prev.offset().top?($right.css("position","fixed"),$right.css("top",0)):($right.css("top",$prev.offset().top),$right.css("position","absolute"))}));